query Manufacturers($bannerCode: BannerCodeEnum!) {
  manufacturers(bannersFilter: [$bannerCode]) {
    nodes {
      base {
        nameSlug
        code
        __typename
      }
      code
      description
      hasProducts
      id
      logoUrl
      name
      nameSlug
      productCountActive
      searchUrlInternal {
        ...searchUrlInternal
        __typename
      }
      websiteUrl
      __typename
    }
    totalCount
    __typename
  }
  viewer(bannerCode: $bannerCode) {
    customerByIdOrDefaultV2 {
      cmsPageBySlug(slug: 
"brands") {
        ...cmsPage
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment searchUrlInternal on UrlStorefrontSearch {
  attributeValueCodes
  branchStock
  categoryCode
  cimIds
  clearance
  crossSell {
    productNumber
    crossSellType
    __typename
  }
  listIds
  madeInUsa
  manufacturerCodes
  newProducts
  normallyStocked
  onSale
  preferredProducts
  previouslyPurchased
  promoIds
  query
  queryWithin
  recentlyViewed
  slug
  __typename
}
fragment cmsPage on CmsPage {
  id
  title
  pageMeta {
    ...pageMetadata
    __typename
  }
  urlExternal {
    location
    __typename
  }
  __typename
}
fragment pageMetadata on PageMetadata {
  description
  pageTitlePartial
  __typename
}
