query AllCategories($bannerCode: BannerCodeEnum!) {
  viewer(bannerCode: $bannerCode) {
    categoriesRoot {
      nodes {
        code
        homePageDescription
        image {
          ...sq90
          __typename
        }
        shortName
        longName
        searchUrlInternal {
          ...searchUrlInternal
          __typename
        }
        sortOrder
        children {
          code
          descendantProductsCount
          shortName
          longName
          searchUrlInternal {
            ...searchUrlInternal
            __typename
          }
          sortOrder
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment sq90 on ProductMediaImage {
  sq90: min(pixels: 90) {
    url
    __typename
  }
  __typename
}
fragment searchUrlInternal on UrlStorefrontSearch {
  attributeValueCodes
  branchStock
  categoryCode
  cimIds
  clearance
  crossSell {
    productNumber
    crossSellType
    __typename
  }
  listIds
  madeInUsa
  manufacturerCodes
  newProducts
  normallyStocked
  onSale
  preferredProducts
  previouslyPurchased
  promoIds
  query
  queryWithin
  recentlyViewed
  slug
  __typename
}
