query ProductByNumber($bannerCode: BannerCodeEnum!, $customerId: ID!, $productNumber: String!, $branchNumbers: [String], $jobAccountNumber: String) {
  viewer(bannerCode: $bannerCode) {
    customerById(customerId: $customerId) {
      productByNumber(productNumber: $productNumber) {
        crossSellSubstitutes(first: 4, viewingBranch: $viewingBranch) {
          totalCount
          nodes {
            ...CrossSellSubstitute
            __typename
          }
          __typename
        }{
        inventoryCompanyWide
        customerPartNumbers
        summary {
          ... on Product {
            alsoKnownAs
            attributes {
              type {
                abbreviation
                description
                displayName
                id
                __typename
              }
              values {
                code
                id
                label
                text
                __typename
              }
              __typename
            }
            isDiscontinued
            isNewProduct
            isOnSale
            isSustainable
            sustainableOfferMessage
            hasLimitedAvailability
            isClearance
            isMadeInUsa
            shortDescription
            boxQuantity
            category {
              ...categoryAncestors
              __typename
            }
            catNum
            documents {
              displayName
              manufacturer {
                name
                __typename
              }
              url
              __typename
            }
            id
            image {
              ...sq420
              ...sq150
              __typename
            }
            longDescription
            manufacturer {
              name
              code
              __typename
            }
            markets
            marketingDescriptionParagraphs
            media {
              ...sq90
              ...sq420
              ...sq640
              ...sq1280
              ... on ProductMediaVideo {
                image {
                  min(pixels: 90) {
                    url
                    __typename
                  }
                  __typename
                }
                vimeoVideoId
                __typename
              }
              __typename
            }
            minimumOrderQuantity
            productNumber
            productNumberFormatted
            reviews {
              averageRating
              bestRating
              worstRating
              lineItems {
                nodes {
                  comments
                  votesHelpful
                  votesNotHelpful
                  rating
                  votesReported
                  source
                  subject
                  submittedDate
                  userDisplayName
                  userLocation
                  __typename
                }
                totalCount
                __typename
              }
              ratingHistogram {
                label
                numberOfReviews
                ratingValue
                __typename
              }
              __typename
            }
            shortDescription
            showOnWeb
            sku
            slangTerms
            title
            upc
            urlExternal {
              location
              __typename
            }
            urlExternalShort {
              location
              __typename
            }
            urlInternal {
              ...urlInternal
              __typename
            }
            pageMeta {
              ...pageMetadata
              __typename
            }
            __typename
          }
          __typename
        }
        prices(
          branchNumbers: $branchNumbers
          cachingOption: CACHE_ONLY
          jobAccountNumber: $jobAccountNumber
        ) {
          price {
            ... on ProductPricingWithAmount {
              amount
              amountFormatted
              decimalPlaces
              extendedAmount
              extendedAmountFormatted
              quantity
              unitOfMeasure
              unitOfMeasureFactor
              __typename
            }
            ... on ProductPricingUnavailable {
              message
              status
              __typename
            }
            __typename
          }
          __typename
        }
        inventoryAtBranches(branchNumbers: $branchNumbers) {
          branchNumber
          total
          __typename
        }
        listLines {
          nodes {
            list {
              title
              id
              __typename
            }
            __typename
          }
          __typename
        }
        manufacturerInventoryAvailable
        preferred
        cuttableFrom(branchNumbers: $branchNumbers)
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment sq90 on ProductMediaImage {
  sq90: min(pixels: 90) {
    url
    __typename
  }
  __typename
}
fragment sq420 on ProductMediaImage {
  sq420: min(pixels: 420) {
    url
    __typename
  }
  __typename
}
fragment sq150 on ProductMediaImage {
  sq150: min(pixels: 150) {
    url
    __typename
  }
  __typename
}
fragment sq640 on ProductMediaImage {
  sq640: min(pixels: 640) {
    url
    __typename
  }
  __typename
}
fragment sq1280 on ProductMediaImage {
  sq1280: min(pixels: 1280) {
    url
    __typename
  }
  __typename
}
fragment searchUrlInternal on UrlStorefrontSearch {
  attributeValueCodes
  branchStock
  categoryCode
  cimIds
  clearance
  crossSell {
    productNumber
    crossSellType
    __typename
  }
  listIds
  madeInUsa
  manufacturerCodes
  newProducts
  normallyStocked
  onSale
  preferredProducts
  previouslyPurchased
  promoIds
  query
  queryWithin
  recentlyViewed
  slug
  __typename
}
fragment urlInternal on UrlStorefrontPage {
  routeId
  page
  slug
  __typename
}
fragment categoryAncestors on ProductCategory {
  code
  shortName
  longName
  sortOrder
  searchUrlInternal {
    ...searchUrlInternal
    __typename
  }
  searchUrlExternal {
    location
    __typename
  }
  ancestors {
    code
    shortName
    longName
    sortOrder
    searchUrlInternal {
      ...searchUrlInternal
      __typename
    }
    searchUrlExternal {
      location
      __typename
    }
    __typename
  }
  __typename
}
fragment pageMetadata on PageMetadata {
  description
  pageTitlePartial
  __typename
}
