query ProductSearchV2($bannerCode: BannerCodeEnum!, $customerId: ID!, $request: ProductSearchRequestInput, $productInfoBranches: [String], $jobAccountNumber: String, $cimMode: Boolean!, $cimProductInfo: [String!]!) {
  viewer(bannerCode: $bannerCode) {
    customerById(customerId: $customerId) {
      productSearchV2(request: $request) {
        productSearchResultsHasTableViewFeatureAccess
        featureBenefitOptions {
          featureBenefitTitle
          featureBenefitDescription
          featureBenefitAttributes {
            isActive
            code
            count
            isActive
            __typename
          }
          featureBenefitManufacturers {
            isActive
            count
            manufacturer {
              name
                            code
              searchUrlInternal {
                ...searchUrlInternal
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        request {
          ...searchRequest
          __typename
        }
        originalRequest {
          ...searchRequest
          __typename
        }
        featuredCategories {
          results {
            categoryCode
            count
            category {
              ...searchCategory
              __typename
            }
            __typename
          }
          __typename
        }
        categories {
          nodes {
            categoryCode
            count
            category {
              ...searchCategory
              __typename
            }
            isFeatured
            __typename
          }
          __typename
        }
        virtualCategories {
          results {
            categoryCode
            count
            category {
              ...searchCategory
              __typename
            }
            isFeatured
            __typename
          }
          __typename
        }
        manufacturers(first: 10) {
          totalCount
          nodes {
            isActive
            count
            manufacturer {
              name
              code
              searchUrlInternal {
                ...searchUrlInternal
                __typename
              }
              __typename
            }
            __typename
          }
          __typename
        }
        attributesAll(first: 8) {
          edges {
            cursor
            __typename
          }
          nodes {
            values(first: 10) {
              totalCount
              nodes {
                attribute {
                  label
                  text
                  code
                  ordinalPosition
                  __typename
                }
                count
                isActive
                __typename
              }
              __typename
            }
            attributeType {
              displayName
              attributeTypeCode
              __typename
            }
            __typename
          }
          __typename
        }
        catalogs {
          totalCount
          nodes {
            count
            isActive
            catalog {
              description
              catalogId
              __typename
            }
            __typename
          }
          __typename
        }
        cims {
          totalCount
          nodes {
            cim {
              cimId
              cimName
              __typename
            }
            count
            isActive
            __typename
          }
          __typename
        }
        lists {
          totalCount
          nodes {
            list {
              id
              title
              __typename
            }
            isActive
            count
            __typename
          }
          __typename
        }
        preferredProducts
        previouslyPurchased
        madeInUsa
        newProducts
        onSale
        clearance
        sustainability
        products {
          totalCount
          nodes {
            product {
              summary {
                ... on Product {
                  attributes {
                    type {
                      displayName
                      __typename
                    }
                    values {
                      text
                      __typename
                    }
                    __typename
                  }
                  manufacturer {
                    name
                    description                    __typename
                  }
                  shortDescription
                  longDescription
                  title
                  catNum
                  upc
                  productNumber
                  productNumberFormatted
                  minimumOrderQuantity
                  isDiscontinued
                  isNewProduct
                  isOnSale
                  isClearance
                  isMadeInUsa
                  isSustainable
                  sustainableOfferMessage
                  image {
                    ...sq150
                    ...sq90
                    __typename
                  }
                  boxQuantity
                  category {
                    shortName
                    longName
                    searchUrlInternal {
                      ...searchUrlInternal
                      __typename
                    }
                    __typename
                  }
                  urlInternal {
                    ...urlInternal
                    __typename
                  }
                  __typename
                }
                __typename
              }
              prices(
                branchNumbers: $productInfoBranches
                jobAccountNumber: $jobAccountNumber
                cachingOption: CACHE_ONLY
              ) {
                price {
                  ... on ProductPricingWithAmount {
                    amount
                    amountFormatted
                    decimalPlaces
                    extendedAmount
                    extendedAmountFormatted
                    quantity
                    unitOfMeasure
                    unitOfMeasureFactor
                    __typename
                  }
                  ... on ProductPricingUnavailable {
                    message
                    status
                    __typename
                  }
                  __typename
                }
                __typename
              }
              customerPartNumbers
              inventoryCompanyWide
              inventoryAtBranches(branchNumbers: $productInfoBranches) {
                branchNumber
                total
                __typename
              }
              cimSolutionProduct(cimIds: $cimProductInfo) @include(if: $cimMode) {
                quantity
                targetQuantity
                location
                cimInventoryId
                __typename
              }
              preferred
              cuttableFrom(branchNumbers: $productInfoBranches)
              __typename
            }
            __typename
          }
          __typename
        }
        pageMeta {
          ...pageMetadata
          __typename
        }
        pageIsNoIndex
        paginationIsNoFollow
        urlExternal {
          location
          __typename
        }
        urlExternalCanonical {
          location
          __typename
        }
        urlExternalShort {
          location
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment searchCategory on ProductCategory {
  searchUrlInternal {
    ...searchUrlInternal
    __typename
  }
  searchUrlExternal {
    location
    __typename
  }
  image {
    ...sq150
    __typename
  }
  longName
  shortName
  __typename
}
fragment searchRequest on ProductSearchRequest {
  doNotModify
  filter {
    attributeValueCodes
    attributeValues {
      code
      label
      text
      type {
        attributeTypeCode
        displayName
        __typename
      }
      __typename
    }
    branchStock
    category {
      ...categoryAncestors
      __typename
    }
    categoryCode
    catalogIdsAvailable
    cimIds
    crossSell {
      product {
        summary {
          ... on Product {
            productNumber
            urlInternal {
              ...urlInternal
              __typename
            }
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    hasQueryAndNoOtherFilters
    isCategoryBrowsing
    isEmpty
    isFilteringByAnyAttributes
    isFilteringByCategory
    isQueryEmpty
    listIds
    lists {
      id
      title
      __typename
    }
    madeInUsa
    manufacturerCodes
    manufacturers {
      name
      code
      __typename
    }
    newProducts
    onSale
    promoIds
    clearance
    sustainability
    preferredProducts
    previouslyPurchased
    query
    queryParsed {
      hasAnyNumbers
      isBlank
      normalized
      words
      __typename
    }
    queryWithin
    queryWithinParsed {
      hasAnyNumbers
      isBlank
      normalized
      words
      __typename
    }
    recentlyViewed
    __typename
  }
  productsPaging {
    page
    pageSize
    __typename
  }
  productsSort
  __typename
}
fragment sq150 on ProductMediaImage {
  sq150: min(pixels: 150) {
    url
    __typename
  }
  __typename
}
fragment sq90 on ProductMediaImage {
  sq90: min(pixels: 90) {
    url
    __typename
  }
  __typename
}
fragment searchUrlInternal on UrlStorefrontSearch {
  attributeValueCodes
  branchStock
  categoryCode
  cimIds
  clearance
  crossSell {
    productNumber
    crossSellType
    __typename
  }
  listIds
  madeInUsa
  manufacturerCodes
  newProducts
  normallyStocked
  onSale
  preferredProducts
  previouslyPurchased
  promoIds
  query
  queryWithin
  recentlyViewed
  slug
  __typename
}
fragment urlInternal on UrlStorefrontPage {
  routeId
  page
  slug
  __typename
}
fragment categoryAncestors on ProductCategory {
  code
  shortName
  longName
  sortOrder
  searchUrlInternal {
    ...searchUrlInternal
    __typename
  }
  searchUrlExternal {
    location
    __typename
  }
  ancestors {
    code
    shortName
    longName
    sortOrder
    searchUrlInternal {
      ...searchUrlInternal
      __typename
    }
    searchUrlExternal {
      location
      __typename
    }
    __typename
  }
  __typename
}
fragment pageMetadata on PageMetadata {
  description
  pageTitlePartial
  __typename
}
