query ProductByNumber($bannerCode: BannerCodeEnum!, $customerId: ID!, $productNumber: String!, $branchNumbers: [String], $jobAccountNumber: String, $viewingBranch: String) {
  viewer(bannerCode: $bannerCode) {
    customerById(customerId: $customerId) {
      productByNumber(productNumber: $productNumber) {
        crossSellSubstitutes(first: 4, viewingBranch: $viewingBranch) {
          totalCount
          nodes {
            ...CrossSellSubstitute
            __typename
          }
          __typename
        }
        crossSellAssociated(first: 4, viewingBranch: $viewingBranch) {
          totalCount
          nodes {
            ...CrossSellAssociatedOthers
            __typename
          }
          __typename
        }
        crossSellOthersAlsoBought(first: 4, viewingBranch: $viewingBranch) {
          totalCount
          nodes {
            ...CrossSellAssociatedOthers
            __typename
          }
          __typename
        }
        __typename
      }
      __typename
    }
    __typename
  }
}
fragment sq90 on ProductMediaImage {
  sq90: min(pixels: 90) {
    url
    __typename
  }
  __typename
}
fragment sq150 on ProductMediaImage {
  sq150: min(pixels: 150) {
    url
    __typename
  }
  __typename
}
fragment urlInternal on UrlStorefrontPage {
  routeId
  page
  slug
  __typename
}
fragment CrossSellAssociatedOthers on CustomerProduct {
  inventoryCompanyWide
  cuttableFrom(branchNumbers: $branchNumbers)
  summary {
    ... on Product {
      isDiscontinued
      isNewProduct
      isOnSale
      isClearance
      isSustainable
      sustainableOfferMessage
      isNewProduct
      isMadeInUsa
      cuttableFromAll
      boxQuantity
      catNum
      image {
        ...sq90
        ...sq150
        __typename
      }
      manufacturer {
        name
        __typename
      }
      minimumOrderQuantity
      productNumber
      productNumberFormatted
      sku
      title
      upc
      urlInternal {
        ...urlInternal
        __typename
      }
      __typename
    }
    __typename
  }
  prices(
    branchNumbers: $branchNumbers
    cachingOption: CACHE_ONLY
    jobAccountNumber: $jobAccountNumber
  ) {
    price {
      ... on ProductPricingWithAmount {
        amount
        amountFormatted
        decimalPlaces
        extendedAmount
        extendedAmountFormatted
        quantity
        unitOfMeasure
        unitOfMeasureFactor
        __typename
      }
      ... on ProductPricingUnavailable {
        message
        status
        __typename
      }
      __typename
    }
    __typename
  }
  inventoryAtBranches(branchNumbers: $branchNumbers) {
    branchNumber
    total
    __typename
  }
  __typename
}
